##### Solution
Declarations:
var aisle: integer;
var section: string;
var aisle_section: string;

Constraints:
aisle = 1..3;
section = "Fantasy", "Hobbies", "Music", "Puzzles", "Reading", "Science";

Options:
section_in_aisle(aisle, section) = section in aisle;
aisle_section(aisle, section) = section in aisle and section!= "Science";

